const crypto = require('crypto');

const n = $input.first().json.topics.length; // total number of topics
const count = 4; // number of random numbers to select

function getRandomInt(max) {
  const randomBuffer = new Uint32Array(1);
  crypto.getRandomValues(randomBuffer);
  return Math.floor(randomBuffer[0] / (0xFFFFFFFF + 1) * max);
}

const randomNumbers = new Set();
while (randomNumbers.size < count) {
  randomNumbers.add(getRandomInt(n));
}

return [{ json: { randomNumbers: Array.from(randomNumbers) } }];

